import React,{ useState,useEffect} from 'react'
import "../App.css"


function Home() {

  const [farmer, setFarmer] = useState([]);
 console.log(farmer)

 useEffect(() => {
    fetch(`http://localhost:9292/farmers`)
    .then(response => response.json())
    .then((data) => {

     setFarmer(data)

    })
  }, [])


  return (
    <div className="app" >
      {/* {<Table data={data} />} */}
        <table>
      <tbody>
        <tr>
          <th>Name</th>
          <th>Tel</th>
          <th>Location</th>
          <th>Product</th>
          <th>Category</th>
          <th>Quantity (Kg)</th>
          <th>Price</th>
        </tr>
        {farmer.map((farmer) => (
          <tr key={farmer.id}>
            <td>{farmer.name}</td>
            <td>{farmer.tel}</td>
            <td>{farmer.location}</td>
            {farmer.products.map((pro)=>
             <>
            <td>{pro.name}</td> 
            <td>{pro.quantity}</td>
            </>)}
           {farmer.category.map((pro)=>
             <td>{pro.name}</td>)}

          </tr>
        ))}
      </tbody>
    </table>
  {/* console.log(data) */}
    </div>
  );
}

export default Home
