import React from 'react'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import {useState} from 'react'
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));



function ProductForms({ textStyle, button,newProduct}) {

const classes = useStyles();
   const [age, setAge] = useState('');

  const handleChange = (event) => {
    setAge(event.target.value);
  };

      const test=(<FormControl className={classes.formControl}>

        <InputLabel id="demo-simple-select-label">Farmer</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={age}
          onChange={handleChange}
        >
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
      )
  const [name, setName] = useState("")
  const [price, setPrice] = useState("")
  const [quantity, setQuantity] = useState("")
  const [category_id, setCategory] = useState("")
  const [farmer_id, setFarmer] = useState("")




   function handleSubmit(event) {
    event.preventDefault();
    let newProduct={
      name:name,
      quantity:quantity,
      price:price,
      category_id:category_id,
      farmer_id:farmer_id
    }


    fetch("http://localhost:9292/p", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newProduct)
    })
    .then(response => response.json())
    .then(data => {
     newProduct(data) 
    })
    setName("")
    setPrice("")
    setQuantity("")
    setCategory("")
    setFarmer("")
  }

  return (
    <div>
      <h2>Add product Form</h2>
      <TextField
        id="outlined-basic"
        label="Name"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setName(e.target.value)}
        value={name}
      />
      <TextField
        id="outlined-basic"
        label="Quantity (kg)"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setQuantity(e.target.value)}
        value={quantity}
      />
      <TextField
        id="outlined-basic"
        label="Price"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setPrice(e.target.value)}
        value={price}
      />
      <TextField
        id="outlined-basic"
        label="Caterogy_id"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setCategory(e.target.value)}
        value={category_id}
      />
      <TextField
        id="outlined-basic"
        label="Farmer_id"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setFarmer(e.target.value)}
        value={farmer_id}
      />
      <Button
        variant="contained"
        color="secondary"
        className={button}
        onClick={handleSubmit}
      >
        {" "}
        Buy product
      </Button>
      {test}
    </div>
  );
}
export default ProductForms
