import React,{useState} from 'react'
import {useNavigation} from 'react-router-dom'
import { Grid,Paper, TextField, Button, Typography,Link } from '@material-ui/core'



function Login(){

    const username="patrick" 
    const password="1234pato"

    const [usernamestate ,setUsername]=useState("")
    const [paswordstate ,setPassword]=useState("")
    const [loggedin ,setLoggedin]=useState(false)

    let navigate=useNavigation()

    function Loggin(){
        if(username==usernamestate && password==paswordstate){
           setLoggedin(true)
        }
    }

    const paperStyle={padding :20,height:'70vh',width:280, margin:"20px auto"}
    const btnstyle={margin:'8px 0'}
    return(
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align='center'>
                    <h2>Sign In</h2>
                </Grid>
                <TextField label='Username' placeholder='Enter username' fullWidth required onChange={(e) => setUsername(e.target.value)} />
                <TextField label='Password' placeholder='Enter password' type='password' fullWidth required onChange={(e) => setPassword(e.target.value)}/>
                <Button type='submit' color='primary' variant="contained" style={btnstyle} fullWidth onClick={Loggin}>Sign in</Button>
                  {Loggin && (navigate('/test'))}
                <Typography >
                     <Link href="#" >
                        Forgot password ?
                </Link>
                </Typography>
            </Paper>
        </Grid>
    )
}

export default Login
