function ProdoctD({farmer}) {

 const [product, setProduct] = useState([]);
 const btnstyle={margin:'8px 0'}



  useEffect(() => {
    fetch(`http://localhost:9292/Product`)
    .then(response => response.json())
    .then((data) => {

     setProduct(data)

    })
  }, [])




  return (

    <div className="app" >

        <table>
      <tbody>
        <tr>
          <th>Name</th>
          <th>Quanity</th>
          <th>Price</th>
          <th>Category</th>
          <th>F</th>

          {/* <th>Category</th>
          <th>Quantity (Kg)</th>
          <th>Price</th> */}
        </tr>
        {farmer.map((farmer) => (
          <tr key={farmer.id}>
            {/* <td>{farmer.name}</td>
            <td>{farmer.tel}</td>
            <td>{farmer.location}</td>
            <td>  <Button type='submit' color='primary' variant="contained" style={btnstyle}>view Products</Button> </td> */}

            {farmer.products.map((pro)=>
             <>
            <td>{pro.name}</td> 
            <td>{pro.category.name}</td>
            <td>{pro.quantity}</td>
             <td>{pro.price}</td>
            </> )}
          </tr>

        ))}
      </tbody>

    </table>
    <Button type='submit' color='primary' variant="contained" style={btnstyle} fullWidth>Total farmers debt: {total}</Button>


    </div>

  );
}


export default ProdoctD
