import React,{useState,useEffect}from 'react';
import {Routes,Route} from "react-router-dom"
import Login from './components/login';
import Home from './components/Home';
import ProductForms from "./components/Productform.js"
import ProtectedRoutes from './components/ProtectedRoutes';
import SearchAppBar from './components/SearchAppBar'
import images from './farm.png'
import Category from './components/Category';
import {useNavigate} from "react-router-dom"
import ProdoctD from './components/ProdoctD';


function App() {

   const [product, setProduct] = useState([]);
   const [loggedin, setLoggedin]=useState(false)
   const [farmer, setFarmer] = useState([]);
   const [category, setCategory] = useState([]);


    let navigate = useNavigate(); 
  const routeChange = () =>{ 
    let path = `newPath`; 
    navigate(path);
  }


    function addProduct(newProduct) {
    setProduct([...product, newProduct])
  }

   function addFarmer(newProduct) {
     setProduct([...product, newProduct]);
   }

   useEffect(() => {
    fetch("http://localhost:9292/farmers")
    .then(response => response.json())
    .then((data) => {

     setFarmer(data)

    })
  }, [])

    useEffect(() => {
      fetch("http://localhost:9292/category")
        .then((response) => response.json())
        .then((data) => {
          setCategory(data);
        });
    }, []);




  return (
    <div className="App">
      <SearchAppBar images={images} />
      <Routes>
        {/* <Route path="/" element= {<Login loggedin={loggedin} logSet={setLoggedin}/>}/>
        <Route element={<ProtectedRoutes loggedin={loggedin}/>}>  */}
        <Route path="/home" element= {<Home farmer={farmer}/>}/>
        <Route path="/views" element= {<ProdoctD farmer={farmer} setFarmer={setFarmer} product={product} setProduct={setProduct}/>}/>
        <Route path="/product" element={<ProductForms newProduct={addProduct} farmer={farmer} category={category}/>}/>

        </Routes>  

    </div>
  );
}

export default App;
