import React from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
import FarmerForm from "./FarmerForm.js";
import { useNavigate } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

function EditProducts({textStyle,button,
  product,
  setProduct,
  name,
  price,
  quantity,
  setName,
  setPrice,
  setQuantity,
}) {
  const btnstyle = { margin: "8px 0" };
  const classes = useStyles();
  let navigate = useNavigate();
  const routeChange = () => {
    let path = `/views`;
    navigate(path);
  };
  function handPatch(id) {
    fetch(`http://localhost:9292/product/${id}`, {
      method: "PATCH",
       headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        // comment: comment,
        // score: score,
      }),
    })
      .then((r) => r.json())
      .then(() => {
        const updateProduct = product.filter((product) => product.id !== id);
        console.log(updateProduct);
      });
  }

  return (
    <div className="app">
      <h2>Add product Form</h2>
      <TextField
        id="outlined-basic"
        label="Name"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setName(e.target.value)}
        value={name}
      />
      <TextField
        id="outlined-basic"
        label="Quantity (kg)"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setQuantity(e.target.value)}
        value={quantity}
      />
      <TextField
        id="outlined-basic"
        label="Price"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setPrice(e.target.value)}
        value={price}
      />
      {/* <TextField
        id="outlined-basic"
        label="Caterogy_id"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setCategory(e.target.value)}
        value={category_id}
      /> */}
      {/* <TextField
        id="outlined-basic"
        label="Farmer_id"
        variant="outlined"
        className={textStyle}
        onChange={(e) => setFarmer(e.target.value)}
        value={farmer_id}
      /> */}
      <Button
        variant="contained"
        color="secondary"
        className={button}
        // onClick={handleSubmit}
      >
       Update Product
      </Button>
    </div>
  );
}

export default EditProducts;
