import React,{ useState,useEffect} from 'react'
import "../App.css"
import { Button } from '@material-ui/core'
import { flexbox } from '@material-ui/system';
import Box from '@material-ui/core/Box';



function Home({farmer ,setFarmer}) {

 const [total, setTotal] = useState([]);
 const [subTotal, setSubTotal] = useState([]);
 console.log(farmer)
 const btnstyle={margin:'8px 0'}


  function handleDelete(id) {

    fetch(`http://localhost:9292/farmers/${id}`, {
      method: "DELETE",
    })
      .then((r) => r.json())
      .then(() => {
        const  deleteFarmer= farmer.filter((product) => product.id !== id);
        setFarmer(deleteFarmer);
      });

}


  useEffect(() => {
    fetch(`http://localhost:9292/total`)
    .then(response => response.json())
    .then((data) => {

     setTotal(data)

    })
  }, [])

  function handleSubtotal(id) {
    fetch(`http://localhost:9292/product/subtotal/${id}`)
    .then(response => response.json())
    .then(() => {
        const  subtotal= farmer.filter((product) => product.id !== id);
        setSubTotal(subtotal);
      });
  }




  return (

    <div className="app" >

     <Box display="flex" p={1} bgcolor="background.paper" flex-direction="column">


        <table>
      <tbody>
        <tr>
          <th>Name</th>
          <th>Tel</th>
          <th>Location</th>
          <th>Product supplied</th>
        </tr>

        {farmer.map((farmer) => (
          <tr key={farmer.id} >
            <td>{farmer.name}</td>
            <td>{farmer.tel}</td>
            <td>{farmer.location}</td>
              <tr>
                 <th>Product</th>
          <th>Quantity (Kg)</th>
          <th>Price</th>
        </tr>
                {farmer.products.map((pro)=>
             <>

               <div>
            <td
            >{pro.name}</td> 
            <td>{pro.quantity}</td>
             <td>{pro.price}</td>
             </div>

            </> 
            )}
            {/* <td><th>{subTotal}</th></td> */}
            console.log
            <td> 
                  <Button type='submit' color='primary' variant="contained" style={btnstyle} onClick={(e) => (
                 handleSubtotal(farmer.id))}>View SubTotal</Button></td> 
            <td> 
                  <Button type='submit' color='primary' variant="contained" style={btnstyle} onClick={(e) => (
                 handleDelete(farmer.id))}>Delete Farmer</Button></td> 


          </tr>

        ))}

      </tbody>

    </table>

     </Box>

    <Button type='submit' color='primary' variant="contained" style={btnstyle} fullWidth>Total Ksh: {total}</Button>


    </div>

  );
}

export default Home
