import React,{ useState,useEffect} from 'react'
import "../App.css"
import { Button } from '@material-ui/core'
import ProductForms from "./Productform.js"
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';

function Home({farmer}) {

 const [total, setTotal] = useState([]);
 console.log(farmer)
 const btnstyle={margin:'8px 0'}


  useEffect(() => {
    fetch(`http://localhost:9292/total`)
    .then(response => response.json())
    .then((data) => {

     setTotal(data)

    })
  }, [])




  return (
    <div className="app" >

        <table>
      <tbody>
        <tr>
          <th>Name</th>
          <th>Tel</th>
          <th>Location</th>
          <th>Product Delivered</th>

          {/* <th>Category</th>
          <th>Quantity (Kg)</th>
          <th>Price</th> */}
        </tr>
        {farmer.map((farmer) => (
          <tr key={farmer.id}>
            <td>{farmer.name}</td>
            <td>{farmer.tel}</td>
            <td>{farmer.location}</td>
            <td>  <Button type='submit' color='primary' variant="contained" style={btnstyle}>view Products</Button> </td>

            {/* {farmer.products.map((pro)=>
             <>
            <td>{pro.name}</td> 
            <td>{pro.category.name}</td>
            <td>{pro.quantity}</td>
             <td>{pro.price}</td>
            </> )} */}
          </tr>

        ))}
      </tbody>

    </table>
    <Button type='submit' color='primary' variant="contained" style={btnstyle} fullWidth>Total farmers debt: {total}</Button>

    </div>

  );
}

export default Home
